package sk.a3soft.kit.public_demoapp.utils

import android.graphics.Bitmap
import android.graphics.BitmapFactory
import android.os.Environment
import android.util.Base64
import java.io.File
import java.io.FileOutputStream
import java.io.IOException

private const val SAMPLE_IMAGE_DATA =
    "data:image/jpeg;base64,"
private val SAMPLE_IMAGE_PATH =
    Environment.getExternalStoragePublicDirectory(Environment.DIRECTORY_DOWNLOADS).path + "/" + "sample-image.png"

object FileUtils {

    @JvmStatic
    fun saveSampleImage(): String? {
        try {
            val pureBase64Encoded = SAMPLE_IMAGE_DATA.substring(SAMPLE_IMAGE_DATA.indexOf(",") + 1)
            val bytes = Base64.decode(pureBase64Encoded, Base64.DEFAULT)
            val decodedBitmap = BitmapFactory.decodeByteArray(bytes, 0, bytes.size)

            FileOutputStream(SAMPLE_IMAGE_PATH).use { out ->
                decodedBitmap.compress(Bitmap.CompressFormat.PNG, 70, out)
            }
        } catch (e: IOException) {
           return null
        }

        return SAMPLE_IMAGE_PATH
    }

    @JvmStatic
    fun deleteSampleImage() {
        try {
            val file = File(SAMPLE_IMAGE_PATH)
            if (file.exists()) file.delete()
        } catch (_: Exception) {}
    }
}